import { useState, useContext, useEffect } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import { useNavigate, useParams } from "react-router-dom";
import { ProductContext } from './ProductContext'
function ProductForm() {
// Original and incorrect attempt
    let {id} = useParams()
// let params = useParams(id)
let [product, setProduct] = useState({
productName: "",
description: "",
price: ""
})
let { getProduct, addProduct, updateProduct } = useContext(ProductContext)
let navigate = useNavigate()
let { productName, description, price } = product
//Patrick's updates and remove context consumer from around the Form below. You will see it there commented out.
// function ProductForm() {
//     // let {id} = useParams()
//     let params = useParams()
//     let [product, setProduct] = useState({
//     id: params.productId,
//     productName: "",
//     description: "",
//     price: ""
//     })
// let { getProduct, addProduct, updateProduct } = useContext(ProductContext)
// let navigate = useNavigate()
// let { productName, description, price } = product
useEffect(() => {
if (id === undefined) return
async function fetch() {
    await getProduct(id)
    .then((product) => setProduct(product))
}
fetch()
}, [id])
    function handleChange(event) {
        setProduct((preValue) => {
            return { ...preValue, [event.target.name]: event.target.value }
            })
    }
    function addOrUpdate() {
    if (id === undefined) {
        return addProduct(product)
    } else {
        return updateProduct(product)
    }
}
    function handleSubmit(event) {
        event.preventDefault()
        addOrUpdate()
    .then((product) =>
    navigate(`/products/${product.id}`)
)
}
    return (
    <ProductContext.Consumer>
        <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" >
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" name="productName" value={productName} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Description</Form.Label>
                <Form.Control type="text" name="description" value={description} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" >
            <Form.Label>Price</Form.Label>
                <Form.Control type="text" name="price" value={price} onChange={handleChange} />
            </Form.Group>
            <Button type="submit">Save</Button>
        </Form>
        </ProductContext.Consumer>
    )
}
export default ProductForm
