import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./Home";
import About from "./About";
import ProductList from "./ProductList";
import Product from "./Product";
import "bootstrap/dist/css/bootstrap.min.css";
import ProductForm from "./ProductForm";
import ProductContext from "./ProductContext";

function App() {
  return (

    <Router>
      <Routes>
        <Route path="/" element={<Home />}>
          <Route index element={<><h1>Restore Beauty</h1><p>Organic, non-toxic, holistic skincare products</p></>} />
          <Route path="about" element={<About />}></Route>
          <Route path="products" element={<ProductList />}>
            <Route index element={<p>Select a Product for more details</p>} />
            <Route path="new" element={<ProductForm />} />
            <Route path=":productId/edit" element={<ProductForm />} />

            <Route path=":productId" element={<Product />} />
            <Route path="*" element={<h1>Product Not Found</h1>} />
          </Route>
        </Route>
        <Route path="*" element={<h1>Page Not Found</h1>} />
      </Routes>
    </Router>

  );
}

export default App;
