import { useState, useContext, useEffect } from 'react';
import {Form, Image} from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import { useNavigate, useParams } from "react-router-dom";
import { ProductContext } from './ProductContext';

function ProductForm() {

    let params = useParams()
    let [product, setProduct] = useState({
    id: params.productId,
    productName: "",
    description: "",
    price: "",
    dairyFree: "",
    imageURL: ""
    })

let { getProduct, addProduct, updateProduct } = useContext(ProductContext)
let navigate = useNavigate()
let { id, dairyFree, imageURL, productName, description, price } = product

useEffect(() => {
if (id === undefined) return
async function fetch() {
    await getProduct(id)
    .then((product) => setProduct(product))
}
fetch()
}, [id])


    function handleChange(event) {
        setProduct((preValue) => {
            return { ...preValue, [event.target.name]: event.target.value }
            })
    }

    function addOrUpdate() {
    if (id === undefined) {
        return addProduct(product)
    } else {
        return updateProduct(product)
    }
}

    function handleSubmit(event) {
        event.preventDefault()
        addOrUpdate()
    .then((product) =>
    navigate(`/products/${product.id}`)
)
}

function getImg() {
    try {
      return (`/products/${product.imageURL}`)
    } catch {
      return "https://via.placeholder.com/256"
    }
  }

    return (

        <Form onSubmit={handleSubmit}>
        {/* <Image src={getImg()} /> */}
            <Form.Group className="mb-3" >
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" name="productName" value={productName} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Description</Form.Label>
                <Form.Control type="text" name="description" value={description} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" >
            <Form.Label>Price</Form.Label>
                <Form.Control type="text" name="price" value={price} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3" >
            <Form.Label>Contains Essential Oils?</Form.Label>
            <Form.Select aria-label="Contains Dairy?" type="radio" name="dairyFree" value={dairyFree} onChange={handleChange}>
                    <option value={true}>true</option>
                    <option value={false}>false</option>
                    <Form.Control type="bool" name="dairyFree" value={dairyFree} onChange={handleChange} />
            </Form.Select>             
            </Form.Group>
            <Form.Group className="mb-3" >
            <Form.Label>Image URL</Form.Label>
                <Form.Control type="text" name="imageURL" value={imageURL} onChange={handleChange} />
            </Form.Group>
            <Button type="submit">Save</Button>
        </Form>


    )
}

export default ProductForm
